или

Case выражение Of
  вариант : оператор;
  ...
  вариант : оператор;
Else оператор
End

Case Ch Of
   'A'..'Z', 'a'..'z' : WriteLn('Буква');
   '0'..'9'           : WriteLn('Цифра');
   '+', '-', '*', '/' : WriteLn('Оператор');
Else WriteLn('Специальный символ')
End;
