Uses Crt;
Var i,j:Integer;

Begin
 For i:=1 to 10 do
  Begin
    ClrScr;
    Write('LOADING');
    For j:=1 to 5 do
     Begin
       Write('.');
       Delay(50000);
     End;
    Delay(30000);
  End;
 ClrScr;
 WriteLn('Press any key to exit');
 Repeat Until KeyPressed
End.
