{Пример программы для функции FileSize}

Uses Crt;

Var  F : File Of Byte;
  Size : LongInt;

Begin
 { Get File name from command line }
 Assign(F, ParamStr(1));
 Reset(F);
 Size:=FileSize(F);
 WriteLn('Размер файла : ', Size, ' байт.');
 WriteLn('Установка указателя позиции на середину файла...');
 Seek(F, Size Div 2);
 WriteLn('Текущая позиция : ', FilePos(F));
 Close(f);
End.
