Var F : File;
 Begin
  {$I-}
  Assign(F, FileName);
  Reset(F);
  Close(F);
  {$I+}
  FileExists := (IOResult = 0) And (FileName <> '');
 End;  { FileExists }
 
 Begin
  { Получаем имя файла из командной строки }
 If FileExists(ParamStr(1)) Then WriteLn('Файл существует.')
                            Else WriteLn('Файл не найден!');
End.
